import React from 'react'
import { useSelector } from "react-redux";

const Cart = () => {
  const cartItems = useSelector((state) => state.cartItems);
  
  return (
    <div>
      <h4>Cart Items:</h4>
      {(cartItems.length) && cartItems.map((item, index)=>{
        return(
          <div key={index}>
            {item.name}
          </div>
        )
      })}
    </div>
  )
}

export default Cart
